"use strict";

document.addEventListener('DOMContentLoaded', function () {
  // height (100vh) fix for IOS
  var vh = window.innerHeight * 0.01;
  document.documentElement.style.setProperty('--vh', "".concat(vh, "px"));

  // resize
  window.addEventListener('resize', function () {
    var vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty('--vh', "".concat(vh, "px"));
  });

  // menu
  var hamburger = document.querySelector('.header__burger');
  var menu = document.querySelector('.nav-mobile');
  var menuClose = document.querySelector('.nav-mobile__close');
  if (hamburger && menu && menuClose) {
    hamburger.addEventListener('click', function (event) {
      event.preventDefault();
      if (!menu.classList.contains('hidden')) {
        menu.classList.add('hidden');
      } else {
        menu.classList.remove('hidden');
      }
    });
    menuClose.addEventListener('click', function (evet) {
      event.preventDefault();
      if (!menu.classList.contains('hidden')) {
        menu.classList.add('hidden');
      } else {
        menu.classList.remove('hidden');
      }
    });
  }

  // slides up/down/toggle
  var slideUpQna = function slideUpQna(target) {
    var duration = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 400;
    target.style.transitionProperty = 'height, margin, padding';
    target.style.transitionDuration = duration + 'ms';
    target.style.height = target.offsetHeight + 'px';
    target.offsetHeight;
    target.style.overflow = 'hidden';
    target.style.height = 0;
    target.style.paddingTop = 0;
    target.style.paddingBottom = 0;
    target.style.marginTop = 0;
    target.style.marginBottom = 0;
    target.previousElementSibling.style.pointerEvents = 'none';
    window.setTimeout(function () {
      target.style.display = 'none';
      target.style.removeProperty('height');
      target.style.removeProperty('padding-top');
      target.style.removeProperty('padding-bottom');
      target.style.removeProperty('margin-top');
      target.style.removeProperty('margin-bottom');
      target.style.removeProperty('overflow');
      target.style.removeProperty('transition-duration');
      target.style.removeProperty('transition-property');
      target.previousElementSibling.style.pointerEvents = 'auto';
    }, duration);
    target.parentNode.classList.remove('is-open');
  };
  var slideDownQna = function slideDownQna(target) {
    var duration = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : 400;
    target.style.removeProperty('display');
    var display = window.getComputedStyle(target).display;
    if (display === 'none') display = 'block';
    target.style.display = display;
    var height = target.offsetHeight;
    target.style.overflow = 'hidden';
    target.style.height = 0;
    target.style.paddingTop = 0;
    target.style.paddingBottom = 0;
    target.style.marginTop = 0;
    target.style.marginBottom = 0;
    target.offsetHeight;
    target.style.transitionProperty = "height, margin, padding";
    target.style.transitionDuration = duration + 'ms';
    target.style.height = height + 'px';
    target.style.removeProperty('padding-top');
    target.style.removeProperty('padding-bottom');
    target.style.removeProperty('margin-top');
    target.style.removeProperty('margin-bottom');
    target.previousElementSibling.style.pointerEvents = 'none';
    window.setTimeout(function () {
      target.style.removeProperty('height');
      target.style.removeProperty('overflow');
      target.style.removeProperty('transition-duration');
      target.style.removeProperty('transition-property');
      target.previousElementSibling.style.pointerEvents = 'auto';
    }, duration);
    target.parentNode.classList.add('is-open');
  };

  // accordeon
  var accordeonTrigger = document.querySelectorAll('.accordion__header');
  if (accordeonTrigger) {
    accordeonTrigger.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        if (!item.parentNode.classList.contains('is-open')) {
          slideDownQna(item.nextElementSibling);
        } else {
          slideUpQna(item.nextElementSibling);
        }
      });
    });
  }

  // apexcharts
  var options = {
    series: [{
      name: "Value",
      data: [10, 20, 30, 40, 50, 60, 70, 80, 90]
    }],
    chart: {
      height: 350,
      fontFamily: 'inherit',
      type: 'area',
      toolbar: {
        show: false
      }
    },
    colors: ['#20c997', '#2f06bb'],
    dataLabels: {
      enabled: false
    },
    stroke: {
      curve: 'straight'
    },
    xaxis: {
      axisBorder: {
        show: false
      },
      categories: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep']
    },
    yaxis: {
      show: false,
      labels: {
        show: false
      },
      axisBorder: {
        show: false
      },
      axisTicks: {
        show: false
      }
    },
    fill: {
      type: 'gradient',
      gradient: {
        opacityFrom: 0.7,
        opacityTo: 0,
        stops: [0, 100]
      }
    },
    grid: {
      position: 'back',
      borderColor: '#F5F5FA',
      strokeDashArray: 4
    },
    legend: {
      horizontalAlign: 'left',
      itemMargin: {
        horizontal: 0,
        vertical: 5
      },
      onItemHover: {
        highlightDataSeries: false
      }
    }
  };
  var dashboardChart = document.getElementById("chart");
  if (dashboardChart) {
    var chart = new ApexCharts(dashboardChart, options);
    chart.render();
  }
});